template.before:
{% if current.collection!="all_tags" %}
{% assign blogs = collections.all  | where:"lang", current.lang | where: "collection", "blog" | where:"tag.id", current.id %}
{% else %}
{% assign blogs = collections.all  | where:"lang", current.lang | where: "collection", "blog" %}
{% endif %}
{% assign sorted_blog = blogs  | sort: 'date' | reverse %}
{% for post in sorted_blog %}

template.replace:
{% assign image=post.image
| directus_asseturl:"module card_projet " %} 
{% assign alt=post.alt_image %}
{% imgtag image, alt, 235, 373%}

