<script data-concat>
// mark links which lead to current page
window.addEventListener('load', function() {
    // Get the current page URL
    const currentUrl = window.location.href;

    // Find all links with the 'data-current-class' attribute
    const links = document.querySelectorAll('a[data-current-class]');

    // Iterate over each link
    links.forEach(link => {
        // Get the URL from the 'href' attribute of the link
        const linkUrl = link.href;

        // Get the class name from the 'data-current-class' attribute
        const className = link.getAttribute('data-current-class');

        // Check if the link URL is the same as the current page URL
        if (linkUrl === currentUrl) {
            // Add the class to the link element
            link.classList.add(className);
        } else {
            link.classList.remove(className);
        }
    });
  	// Sticky menu disappears when scroll down
    let lastScrollTop = 0;
    const navbars = document.querySelectorAll('.hide-on-scroll');
    window.addEventListener('scroll', () => {
        let scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        if (scrollTop > lastScrollTop) {
          // Scroll down
          if(scrollTop > 200) {
            // More than the nav
            navbars.forEach(navbar => {
              navbar.style.top = '-150px';
              navbar.style.opacity = '0';
            });
          }
        } else {
            // Scroll up
            navbars.forEach(navbar => {
              navbar.style.top = '';
              navbar.style.opacity = '1';
            });
        }
        lastScrollTop = scrollTop;
    });
});  
</script>
